mapper.insert:
INSERT INTO follows (
    follower_id,
    following_id,
    created_at
)
VALUES (
           #{followerId},
           #{followingId},
           #{createdAt}
       )

mapper.delete:
DELETE FROM follows
WHERE follower_id = #{followerId}
  AND following_id = #{followingId}

mapper.select:
SELECT COUNT(1)
FROM follows
WHERE follower_id = #{followerId}
  AND following_id = #{followingId}

mapper.delete:
DELETE FROM follows
WHERE follower_id = #{userId}
   OR following_id = #{userId}
