mapper.update:
UPDATE users
nickname = #{nickname}, profile_image_url = #{profileImageUrl}, status_message = #{statusMessage}, profile_theme = #{profileTheme}, avatar_decoration = #{avatarDecoration}, popover_decoration = #{popoverDecoration}, updated_at = #{updatedAt}
WHERE id = #{id}

UPDATE users
SET email = #{email},
    nickname = #{nickname},
    updated_at = #{updatedAt}
WHERE id = #{id}
